def print_available_optimizers():
    """Print all available optimizer configurations in a human-readable format."""
    available = list_available_optimizers()

    print("Available optimizer configurations:")
    print("==================================")

    for optimizer_type, variants in available.items():
        print(f"\n{optimizer_type.upper()}:")
        for variant in variants:
            try:
                config = get_optimizer_config(optimizer_type, variant)
                optimizer_name = config['optimizer_class'].__name__
                lr = config['optimizer_kwargs'].get('lr', 'N/A')
                scheduler = config['scheduler_class'].__name__ if config['scheduler_class'] else "None"

                print(f"  - {variant}: {optimizer_name} (lr={lr}, scheduler={scheduler})")
            except:
                print(f"  - {variant}: [Error loading configuration]")

